INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (1, 'جلسه 1-مشاوره انگیزشی', 'Motivational-counseling', 'fa', '<h2 dir="rtl"><span style="font-size: 18px;"><strong>جلسه 1-مشاوره انگیزشی 1 :</strong></span></h2>
<p dir="rtl"><br /><br /><span style="font-size: 18px;">اعتیاد یعنی قفل و هر قفلی کلیدی دارد</span><br /><br />&nbsp;</p>
<p dir="rtl" style="text-align: center;"><span style="font-size: 18px;"><img style="height: 353px; width: 300px;" src="../../../../../upload/images/2020/mi1_1.jpg" /></span></p>
<p dir="rtl">&nbsp;</p>
<p dir="rtl"><span style="font-size: 18px;">اعتیاد چیست ؟<br /><br />کدامیک پیروز می شوند!</span></p>
<p dir="rtl" style="text-align: center;"><span style="font-size: 18px;"><img style="height: 521px; width: 649px;" src="../../../../../upload/images/2020/mi1_2.jpg" /></span></p>
<p dir="rtl">&nbsp;</p>
<p dir="rtl"><br /><br /><span style="font-size: 18px;">زمانی میتوانید بر اعتیاد پیروز شوید اگر شما :<br /><br />1-اراده داشته باشید و تصمیم بگیرید<br />2-هدف داشته باشید<br /><br /><br />هدف چیست ؟ تغییر<br /><br /><br />&laquo;چرا تغییر؟&raquo;<br /><br /><br />آیا مشکلات قانونی را می دانید ؟<br /><br />طی 3 سال گذشته 1میلیون نفر در ایران در رابطه با مواد مخدر دستگیر شده اند.<br /><br />پس چه باید کرد ؟؟؟ تغییر</span><br /><br /><br />&nbsp;</p>
<p dir="rtl" style="text-align: center;"><span style="font-size: 18px;"><img style="height: 447px; width: 759px;" src="../../../../../upload/images/2020/mi1_3.jpg" /></span></p>
<p dir="rtl">&nbsp;</p>
<p dir="rtl"><br /><span style="font-size: 18px;">رسیدن به تغییر است<br />یک ضرب المثل هندی می گوید :<br />هر کاری و خواسته&rlm;ای نیازمند ۱۰۰۰ روز فعالیت و تلاش است<br /><br />پس<br /><br />برای تغییر باید تلاش کنید<br />چه باید کرد؟<br /><br />1-گوش شنوا داشته باشید .<br />2-حرفهای پزشک را گوش دهید<br />3-از دودلی و بی تفاوتی پرهیز کنید<br />4-به مطالب جلسات مشاوره عمل کنید</span><br />&nbsp;</p>
<p dir="rtl" style="text-align: center;"><span style="font-size: 18px;"><img style="height: 355px; width: 559px;" src="../../../../../upload/images/2020/mi1_4.jpg" /></span></p>
<p dir="rtl">&nbsp;</p>
<p dir="rtl"><span style="font-size: 18px;">یک ضرب المثل می گوید:<br />کفش پا کردن بهتر از فرش نمودن کل زمین است<br />پس باید :<br /><br />خودتان پاک باشید و کاری به محیط زندگی تان نداشته باشید و در تمامی مراحل زندگی، اولویت با درمان می باشد .<br /><br />درمان واقعی اعتیاد ، تغییر انسانی توانمندتر و شایسته تر است که خصوصیات و شخصیت جدیدش خودبخود او را از اعتیاد دور نگاه می دارد . در واقع اعتیاد زیربناهای شخصیتی خاصی دارد مانند صبر کم یا خودخواهی ، خودسری ، انتقادپذیر نبودن و اگر فردی بدون تغییر دادن این زیربناهای شخصیتی فقط به درمان علائم خماری مانند پا درد و بیقراری و بیخوابی اکتفا کند ، پس از مدتی دوباره به اعتیاد روی خواهد آورد .<br /><br />آنهایی که موفق به ترک اعتیاد شده اند نه تنها می توانند به درمان خود برای تبدیل شدن به انسانی شایسته تر و بهتر بپردازند بلکه می توانند برای خدمت جامعه یا میهن خود برای تغییر و تبدیل آن به جامعه آرمانی تلاش نمایند .<br /><br />در اینجا پیشنهاداتی در این زمینه بیان شده است که خود شما نیز ممکنست مواردی به آنها بیافزایید:<br /><br />1- سعی در تمیز و پاکیزه نگه داشتن محیط اطراف خود و جامعه ای که در آن زندگی می کنیم .<br />2- رعایت کامل همیشگی قوانین و مقررات راهنمایی و رانندگی<br />3- کمک به همنوعان<br />هر شب از خود بپرسید " امروز به چه کسی کمک کردم ؟ " هرگز اجازه ندهید روزی بگذرد و به هیچ کس کمک نکرده باشید .<br />4- وقت شناسی ، نظم ، مرتب بودن</span><br /><br /><br />&nbsp;</p>
<p dir="rtl" style="text-align: center;"><span style="font-size: 18px;"><img style="height: 434px; width: 400px;" src="../../../../../upload/images/2020/mi1_5.jpg" /></span></p>
<p dir="rtl">&nbsp;</p>
<p dir="rtl"><span style="font-size: 18px;">5- ادب و احترام :<br /><br />چه در خانواده و چه در جامعه با همه با ادب و احترام رفتار کنید . برای صحبت کردن از کلامی زیبا بهره بگیرید و از مسخره کردن دیگران خودداری کنید . علاوه بر آن سعی کنید شاد باشید و این شادی را به دیگران هم منتقل کنید .<br />6- بهداشت :<br /><br />رسیدگی کامل به بهدات شخصی و ظاهری آراسته داشتن که ویژگی بسیار ارزنده ای است . بنابراین هرگز در حمام رفتن ، کوتاه کردن ناخن ها ، مسواک زدن ، لباس مرتب پوشیدن ، واکس زدن کفش ها و چیزهایی از این قبیل سهل انگاری نکنید .<br /><br />7- راستگویی و پرهیز از دروغ<br />8- ورزش : حداقل سه بار در هفته هر بار حداقل نیم ساعت<br />9- تلاش به یادگیری هنر ، دانش و مهارتهای جدید<br />10- مطالعه : اختصاص حداقل یکساعت در روز به مطالعه<br /><br />11- سپاسگزاری :<br /><br />قدرشناسی و سپاسگزاری از مهمترین ویژگیهایی است که باید در شما شکل بگیرد . باهرکسی که تاکنون کاری برای شما انجام داده تماس بگیرید و از وی تشکر کنید . از این پس نیز قدرشناس تلاشها و محبتهای دیگران باشید .<br /><br /><br />رعایت کردن این موارد به برطرف نمودن برخی ویژگیهای شخصیتی مانند عصبی بودن و تحریک پذیری بیش از حد ، پایبندی کم به ارزشهای معنوی و قواعد و قوانین اجتماعی ، عدم توجه به ظاهر خود ، مسئولیت پذیری کم در قبال دیگران ، انتقادپذیر نبودن ، خودسری ، خودمحوری و غیره کمک زیادی کند .</span><br /><br /></p>', 'article/images/images_1_ITMbHnj.jpg', 379, 0, '2020-04-22 11:26:07', '2022-07-13 08:45:33.573669', 2);
INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (3, 'دعای آرامش', 'Peace-prayer', 'fa', '<p dir="rtl"><span style="font-size: 18px;">خداوندا، آرامشی عطا فرما تا بپذیرم آن چه را که نمی توانم تغییر دهم، شهامتی که تغییر دهم آنچه را که می توانم ومعرفتی که تفاوت این دو را بدانم.</span><br /><br />&nbsp;</p>
<p dir="rtl" style="text-align: center;"><span style="font-size: 18px;"><img style="height: 525px; width: 700px;" src="../../../../../upload/images/2020/matrix20_1.jpg" /></span></p>
<p dir="rtl"><br />&nbsp;</p>
<h2 dir="rtl"><span style="color: #ffffff;"><strong><span style="font-size: 22px;"><span style="background-color: #2980b9;">&nbsp; تکلیف جلسه بيستم&nbsp;&nbsp;</span></span></strong></span></h2>
<p dir="rtl"><br />&nbsp;</p>
<h3 dir="rtl"><span style="font-size: 18px;">لطفا جدول زیر را کامل کنید ، سپس دکمه ثبت را فشار دهید:</span></h3>', 'article/images/images_3.jpg', 316, 0, '2020-04-23 12:56:47', '2022-07-13 08:51:35.276232', 2);
INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (8, 'شادکامی', 'happiness', 'fa', '<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">عوامل موثر بر شادکامی </span></span></strong></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">یافته ها نشان می دهد که مردم شادمان خودشان آن را فراهم کرده اند و شادکامی امری تصادفی و یا ژنتیک و یا غیر انتخابی نیست. شاید به نظر برسد مردم شاد بهترین چیزها را در زندگی دارند ولی حقیقت این است که آنها از آنچه دارند بیشترین بهره و لذت را می برند. مطالعه اصول زیر و پیروی از آن به شما کمک می کند تا&nbsp; به زندگی شادمانه تری دست بیابید.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: center;"><img style="height: 307px; width: 470px;" src="../../../../../upload/images/2020/Happiness2.jpg" alt="" /></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">۱- خانواده:</span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"> کسانی که در روابط زوجی و خانوادگی خود موفق هستند و زندگی خانوادگی و زوجی برای آنها اولویت اول است شادتر هستند.&nbsp; مطاله موسسه گالوپ نشان می دهد هیچ چیز به اندازه زندگی خانوادگی در شادکامی نقش ندارد. این بررسی نشان می دهد اگر چه عواملی مانند موفقیت، خوش شانسی، شهرت ، ثروت، آسایش و خیلی چیزهای دیگر برای بشر لذت بخش است ولی مهمترین عامل شادکامی زندگی خانوادگی است. البته در این مورد چند نکته قابل بررسی است:</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">بررسی ها نشان می دهد افراد متاهل شادتر از مجردها، مطلقه ها و یا بیوه ها هستند. در بین خانواده ها، شادکامترین زوج هایی، کسانی هستند که فرزندان آنها بعد از رشد و کمال خانه را ترک گفته اند و ناشادترین افراد جامعه مادران ازدواج نکرده و یا تک والد ها هستند<strong>. البته تاهل یا رابطه زوجی به تنهایی منبع شادمانی نیست بلکه وجود رابطه رضایتمند جزء لازم شادکامی است</strong>. آنچه زندگی زوجی را شادمانه می کند وجود گرمی، صمیمیت حمایت، یاری و همراهی است. در رابطه خانوادگی داشتن فرزند اگر چه در شادکامی نقش دارد ولی مساوی با شادمانی نیست و بسیار از زوجهای بدون فرزند هم زندگی شادمانه ای دارند. رابطه جنسی سالم نیز یکی از کلیدهای شادکامی است.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">۲- کار:</span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"> بعد از خانواده مهمترین عامل شادکامی، کار است. کار به خودی خود می تواند حس مفید بودن و مولد بودن در انسان ایجاد کند. البته کار در شان و علاقه فرد، بیشترین تاثیر را دارد. کار به انسان این امکان را می دهد که خود را جزئی ازجامعه ببیند و از نظر برخی روانشناسان کار خود علاج بخش برخی از بیماریها و ناراحتی های روانی است. کار وسیله ای است برای بروز استعدادها و دستیابی به خود شکوفایی و نیز در آمد ناشی از کار، برآورده کننده ی نیازهای اساسی انسان و دستیابی به ابزارهای دیگر شادمانی است. اگر چه نوع کار در میزان شادکامی نقش دارد ولی در هر صورت هر نوع کاری بهتر از بی کاری و احساس بیهودگی است.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">3-روابط اجتماعی</span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">: افرادی که اجتماعی و زود جوش هستند و با جامعه به طور رسمی و غیر رسمی ارتباط دارند، شادتر هستند (رسمی مانند کار و فعالیت های مربوط به آن و غیر رسمی مانند کمک به دیگران و روابط خانوادگی و دوستان) آنها از روابط اجتماعی خود لذت می برند و در این روابط موفق هستند. آنها اصول روابط اجتماعی آگاهند بنابراین می توانند روابط جدید ایجاد کنند و نیز روابط خود را تداوم ببخشند.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">4- شرایط اقتصادی:</span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"> بسیاری اوقات ما تلاش می کنیم به خود بقبولانیم که رابطه ای بین مسائل اقتصادی و شادی وجود ندارد اما تحقیقات نشان می دهد افراد فقیر از امکانات به مراتب کمتری برای سلامتی، ارضای نیازهای اساسی، آموزش و پیشرفت خود دارند آمار جرم، اعتیاد و از هم باشیدگی خانوادگی در این دسته افراد بیشتر است. این فرض درست است که پول به خودی خود شادی آور نیست ولی میزانی از امکانات و پول لازم است تا زمینه های شادی، فراهم شود. بدهکاری، قرض و بیماری منابع اصلی استرس و ناشادی هستند.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: black;">5- داشتن برنامه&zwnj;ریزی مناسب</span></span></span></strong><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: purple;">:</span></span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"> برنامه&zwnj;ریزی در انجام امور، شادابی و انرژی مثبت به دنبال داشته، در كاهش نگرانی&zwnj;ها و تشویش&zwnj;های فرد نسبت به آینده نقش بسزایی دارد و در فرد اعتماد به نفس و ایمان به موفقیت به وجود می&zwnj;آورد.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">6- مثبت اندیشی و فعال بودن</span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">: خوش بینی یا مثبت اندیشی یکی از مهمترین عوامل شادکامی است چناچه می توان آن را حلقه وصل دیگر عوامل به شادکامی دانست. این بدان معنی است که اگر فردی همه ملزومات شادکامی مانند ازدواج خوب، شغل مناسب، درآمد کافی و دوستان شاد را داشته باشد ولی نگرش او نسبت به زندگی منفی باشد از امکانات خود لذت نمی برد و به ممکن است که امکانات خود را از دست بدهد. افراد شاد اغلب اوقات مشغول کار، مردم، خانواده، و فعالیت های گوناگون هستند. آنها انسانهایی جستجو گر بوده و زندگی را با قدرت به پیش می برند، به مشکلات دست و پنجه نرم می کنند و زندگی را از درون مشکلات می سازند. مردم شاد بیشتر از زندگی دریافت می کنند چرا که بیشتر به آن می پردازند.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">7- خود بودن:</span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"> افراد شاد با خود راحت هستند خود را می شناسند با خود صادق هستند، با آنچه دردرونشان می گذرد به نحو احسن روبه رو می شوند و می پذیرند، اتکا به خود، انگیزش های خود جوش، راحت بودن با خود و دیگران، برون گرایی و زندگی طبیعی، و عدم مقایسه خود با دیگری و احساس منحصر به فرد بودن از ویژگی ها خود بودن است. </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">دستیابی به شادکامی مستلزم تمرکز، مهارت و تمرین است</span></span></strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: justify;"><strong><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">#سارا دیوبند_روانشناس</span></span></span></span></strong></p>', 'article/images/images_5.jpg', 203, 0, '2020-08-20 06:00:21', '2022-07-13 08:56:07.919853', 2);
