INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (77, 'مصاحبه انگیزشی', 'Motivational-interview', 'fa', '<p dir="rtl"><span style="font-size: 18px;">مصرف مواد برای بسیاری از مصرف کنندگان در ابتدا خوشایند بوده و برای آن ها جنبه های به ظاهر مثبتی دارد در واقع اگر اثرات مثبت اولیه نمی بود، اکثر افراد مصرف مواد را ادامه نمی دادند. اثراتی مثل افزایش انرژی، کاهش نیاز به خواب ، بیشتر شدن اعتماد به نفس ، بهبود روحیه و پنها ن شدن برخی صفات اذیت کننده) مثل کم رویی، گوشه گیری( از جمله اثرات مهمی هستند که بسیاری از مصرف کنندگان به آن ها اشاره دارند .<br /><br />در جریان درمان شما نباید از بیان و ذکر این اثرات خجالت کشیده یا آن ها را پنهان کنید . در هر حال درابتدای امر، شاید باور شما بر این بوده که مواد مخدر فوایدی نیز دارد و به شما کمک میکرد.</span><br /><br />&nbsp;</p>
<p dir="rtl" style="text-align: center;"><span style="font-size: 18px;"><img style="height: 228px; width: 350px;" src="../../../../../upload/images/2020/mi2_1.jpg" /></span></p>
<p dir="rtl">&nbsp;</p>
<p dir="rtl"><span style="font-size: 18px;">بعد از قطع مصرف ، بسیاری از عوارض اعتیاد بر طرف می شوند. مثلا ظاهر شما بهبود می یابد و از فشار مالی تهیه مواد راحت می شوید. اما همزمان ممکن است ولو به صورت کوتاه مدت ، مشکلاتی نیز ظاهر شود . مثلا خواب و اشتهای شما بهم بخورد ، گوشه گیر یا عصبی شوید و از خستگی و ضعف رنج ببرید.<br /><br />بنابراین شما با قطع مصرف ، مواردی را به دست می آورید و احتمالاً برخی چیزها را از دست میدهید.<br /><br />با توجه به مجموعه آنچه به دست می آورید و آنچه موقتاً از دست می دهید، چه قدر علاقمند به ادامه راه هستید؟<br /><br />اما نکته مهم این است که این اثرات خوشایند مصرف مواد ، با گذشت زمان و ادامه مصرف، سریعاً مستهلک شده و عوارض منفی ظاهر می شود. به احتمال بسیار زیاد شما هم این مسئله را تجربه کرده اید.<br /><br />مصرف کنندگان فرصت ها و منابعی را از دست می دهند. عده ای از رفتن اعتبار شکایت دارند . برخی خسته شدن از تهیه مواد را عامل ناراحت کننده می دانند .<br /><br />برای عده ای فشار مالی تهیه مواد و ورشکستگی از عوارض ناراحت کننده مصرف است.</span><br />&nbsp;</p>
<h3 dir="rtl">&nbsp;</h3>', 'article/images/images_2.jpg', 304, 0, '2020-04-22 12:44:55', '2022-07-13 08:50:20.663683', 2);
INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (82, 'درمان کم رویی', 'Treatment-of-shame', 'fa', '<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;"><strong><span style="color: #7030a0;">چطور بر خجالت و کم رویی غلبه کرده و آن را درمان کنیم؟</span></strong></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">آیا کمرو هستید؟ </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">اگر این چنین است، شما تنها نیستید. بسیاری از مردم در جهان از کم رویی خفیف تا شدید رنج می برند و در تلاش برای غلبه بر آن هستند. برای غلبه بر کم رویی، نیاز به درک شرایطی دارید که باعث کم رویی شما می شوند. </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">به یاد داشته باشید که شکستن پوسته تان ، جادویی نیست و یک شبه اتفاق نمی افتد طول می کشد، تلاش و البته تمایل به تغییر می خواهد.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: center;"><span style="font-size: 18px;"><img style="height: 242px; width: 350px;" src="../../../../../upload/images/2020/timidity.jpg" /></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;"><strong><span style="color: #7030a0;">1 . فهمیدن و شناخت ریشه کم رویی خودتان:</span></strong></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">در مورد ریشه کم رویی خود فکر کنید ، کم رویی لزوما معادل درون گرا بودن یا خود را دوست نداشتن، نیست و به این معنی است که به دلایلی خجالت زده و اذیت می شوید.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">ریشه کم رویی شما چیست؟ </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">این به طور کلی علامت یک مشکل بزرگتر است ، در اینجا چند گزینه وجود دارد:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">الف-شما یک تصویر منفی از خودتان دارید:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">این اتفاق زمانی می افتد که ما خودمان را ارزیابی منفی می کنیم . افکاری که مداوم به سرمان می آید گاهی شامل افکار منفی نسبت به خودمان می باشد.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">این وضعیت سبب کاهش اعتماد بنفس و کمررویی در ما می شود .</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">ب-شما توسط دیگران خجالت می کشید:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">گاهی اوقات، هنگامی که کودک هستیم، خجالتی هستیم. متأسفانه مردم روی آن موضع قرار می گیرند و حتی اگر شخصیت رشد کند، همان گونه با ما رفتار می کنند . این باعث می شود که دیگران شما را در جای خود نگه دارند.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: center;"><span style="font-size: 18px;">&nbsp;<img style="height: 204px; width: 400px;" src="../../../../../upload/images/2020/timidity2.jpg" /></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">2.&nbsp; تصویر کم رویی خودتان را بپذیرید:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">یکی از اولین قدم ها برای غلبه بر کم رویی تان این است که سعی کنید احساس خجالت خود را بپذیرید و با آن راحت باشید. هر چه بیشتر شما ناخودآگاه یا آگاهانه با آن مقاومت کنید، بزرگتر خواهد شد. اگر شما خجالتی هستید، آن را بپذیرید. یکی از راه های انجام این کار این است که با خودتان بگویید ''بله من خجالتی هستم و این را قبول دارم''. </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">با خود بگویید : "من می توانم با تمریناتی مستمر آنرا درمان کنم."</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">3.&nbsp; لیستی از موقعیت هایی را که موجب اضطراب شما می شود، تهیه کنید:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">آیا شما در مقابل مخاطبان جدید کم رو می شوید؟ </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">هنگامی که در حال یادگیری یک مهارت جدیدی هستید؟ </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">هنگامی که در حال تجربه یک وضعیت جدیدی هستید؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">وقتی که توسط افرادی که می شناسید محاصره شده اید و تحسینتان می کنند؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">وقتی در جایی کسی را نمی شناسید؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">آنها را مرتب کنید تا اولین چیزهایی که باعث کمترین اضطراب شما می شوند، اول باشند و کسانی که بیشترین اضطراب را به شما وارد می کنند، آخرین باشند. </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">4 . مواجهه سازی :</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">مواجهه به چند دلیل موثر است:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">اول- خوگیری: اگر در موقعیتی که شما را مضطرب می کند بمانید نهایتا سطح اضطراب کاهش می یابد چون بدن پس از مدتی بصورت اتوماتیک برای مقابله با پاسخ های ترس، فشار خون ، ضربان قلب و نرخ تنفس را کاهش می دهد. ولی اگر به مدت کافی در موقعیت نمانید و یا مرتب افکار اضطراب زا را با خود مرور کنید ممکن است این اتفاق نیفتد</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">دوم- تمرین: مواجهه به شما کمک می کند بر مولفه رفتاری اضطراب غلبه کنید چون به شما فرصت می دهد تمرین کنید که در موقعیت هایی که دچار اضطراب می شوید چه بگویید و یا چطور رفتار کنید.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">ابتدا یک لیست از 10-15 وضعیت استرس زا را بنویسید. سپس یک پلکان اضطراب درست کنید که در آن آسان ترین موقعیت ها در پایین این پلکان و دشوارترین آنها در بالای پلکان است.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">حالا از پلکان اول شروع کنید و سپس پله پله بالا بروید. بتدریج رفتارهای کمررویی خود را کاهش دهید .</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">هر بار فقط یک قدم بردارید .</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">شما باید مرتب تمرین کنید </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">یادتان باشد که سایر مردم نیز در موقعیت های اجتماعی احساس اضطراب می کنند آنها فقط آن را نشان نمی دهند. شما تنها فردی نیستید که کمررویی دارید.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">تمرین :</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">1) تصور کنید در پلکانی که انتخاب کرده اید، هستید. همانطور که این موقعیت را تصور می کنید به افکار خود توجه کنید و حداقل2-3 مورد از آنها را بنویسید. به احساس هایی که این افکار را در شما ایجاد می کند، توجه کنید و آنرا بنویسید</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">2) یک یا دو فکر را که به نظر می رسد از همه مهمتر است انتخاب و آنها را با سوالات به چالش بکشید و حتما به آنها پاسخ دهید</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">3) سوالات زیر برای به چالش کشیدن افکار استفاده کنید. </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">1. چه شواهدی وجود دارد که نشان می دهد افکار من درست است؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">2. چه شواهدی نشان می دهد افکار من نادرست است؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">3. این افکار چه فایده ای دارد؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">4. این افکار چه ضررهایی دارد؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">5. چطور می توانم فکر کنم که منطقی تر و مفیدتر باشد؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">4) حالا نتیجه این به چالش کشیدن را در یک پاسخ منطقی خلاصه کنید تا بتوانید آن را در موقعیت مورد نظر با خود تکرار کنید. </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: center;"><span style="font-size: 18px;">&nbsp;<img style="height: 229px; width: 400px;" src="../../../../../upload/images/2020/timidity3.jpg" /></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">درمان کم رویی با چه تمریناتی ممکن است؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ذهنتان را تسخیر کنید :</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">از این خجالت به عنوان نشانه استفاده کنید ؛ هرچیزی که در درون شماست، باعث خجالت می شود زیرا ما آن را به عنوان یک عامل برای کم رویی می بینیم به همین ترتیب ذهن ما نیز می تواند برنامه ریزی شده باشد.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">به طریقی، از دوران کودکی ما برنامه ریزی شده بودیم تا به محرک های خاصی نظیر دور بودن از غریبه ها، ارتفاع ها، حیوانات خطرناک و غیره واکنش نشان دهیم. ما اغلب به طور خودکار واکنش نشان می دهیم، واکنش به طور طبیعی (یا به طور پیش فرض)&nbsp; است و این واکنش می تواند ناقص باشد به عنوان مثال:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">هنگامی که مردم یک مارمولک را ببینند، برخی یک خزنده زشت را می بینند، در حالی که بعضی دیگر یک حیوان خانگی زیبا را می بینند این تفاوت از خاطرات و تجربیات آنها (یا کم تجربگی آنها) با محرک ها (مارمولک) می آید.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">به همان شیوه، زمانی که افراد خجالتی، مردم را می بینند (محرک ها) پاسخ طبیعی آنها خجالت و کمرویی است حقیقت این است که شما می توانید این پاسخ را با تجدید برنامه ریزی در ذهنتان تغییر دهید.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; از خودتان سوال کنید و اعتبار دلایل خود را بررسی کنید:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">برای مثال، ضروری است که برای غلبه بر مشکل خجالتتان با مردم صحبت کنید، و زمانی که احساس خجالت می کنید، سعی کنید این خجالت را به عنوان یک نشانه برای سختی خودتان مطرح کنید. زمانی که شما احساس خجالت و کم رویی می کنید، احتمالا به مکان آرام و ساکتی می روید زیرا این مدت زمان زیادی می با شد که تا به حال واکنش پیش فرض شما بوده است اما این بار زمانی که احساس خجالت می کنید، خودتان را هل دهید و مخالفت کنید، یعنی با مردم صحبت کنید بله، شما احساس بسیار ناراحت کننده ای خواهید داشت، اما با ادامه و تکرار آن ، این احساس ناراحت کننده کاهش می یابد</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; موفقیت را تجسم کنید :</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">چشمانتان را ببندید و وضعیتی را که ممکن است در آن خجالت بکشید را تجسم کنید حالا در ذهنتان، در مورد اعتماد به نفس خود فکر کنید. این کار را زیاد انجام دهید و برای شرایط مختلف انجامش دهید ، این کار موثرتر خواهد بود اگر شما آن را روزانه انجام دهید، خصوصا در صبح.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">ممکن است احساستان احمقانه باشد، اما ورزشکاران از تجسم برای توسعه مهارت های خود استفاده می کنند، پس چرا شما انجام ندهید؟ </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">تمام حواس خود را درگیر کنید تا آن را واقعی تر احساس کنید:در مورد شاد و راحت بودنتان فکر کنید.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">چه احساسی دارید؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">چه کار می کنید؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">به این ترتیب زمانی این اتفاق ها پیش بیاید، راحت تر و آماده تر خواهید بود.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; با خودتان برای واضح صحبت کردن تمرین کنید :</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">این کار&nbsp; به جلوگیری از خجالت و کم رویی شما کمک می کند و نیاز به تکرار آنچه که یواش با خودتان در سکوت پچ پچ می کنید دارد. شما باید صدای خود را بشنوید. حتی باید دوستش داشته باشید، صدای حرف زدن خودتان را ضبط کنید، اما شما متوجه الگوها خواهید شد، در چه زمانی و چرا آن را رها می کنید، چه زمانی فکر می کنید که صدایتان بلند می شود و غیره.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">در ابتدا احساس یک بازیگر را دارید (و انجام چیزهایی که بازیگران در لحظه انجام می دهند)، اما این به یک عادت تبدیل خواهد شد. تمرین باعث عادت می شود.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; خودتان را با دیگران مقایسه نکنید:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">هرچه بیشتر خودتان را با دیگران مقایسه کنید، بیشتر احساس می کنید که قابل قیاس با بقیه نیستید و بیشتر می ترسید که باعث می شود کم رو شوید.&nbsp; اما اگر انجام دادید، واقع بینانه انجام دهید .</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">این را بدانید&nbsp; که هر کسی ،&nbsp; مشکلات خودش را دارد!</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: center;"><span style="font-size: 18px;">&nbsp;<img style="height: 209px; width: 400px;" src="../../../../../upload/images/2020/timidity4.jpg" /></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">6.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; فکر کنید که در مورد چه چیزی عالی هستید:</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">فکر کنید، به جای فکر و نگرانی در مورد ظاهرتان، صدایتان یا لباستان. به یاد داشته باشید که هر کسی، حتی "افراد زیبا" چیزی در مورد خود و یا زندگیشان دارند که دوستش ندارند.</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">هیچ دلیلی وجود ندارد که چرا "مشکل" شما باید خجالت کشیدن و کم رویی باشد، در حالی که "مشکل" بقیه باعث خجالت و کم رویی شان نمی شود. </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">ارزش اجتماعی و نقاط قوت خود را شناسایی کنیدآیا شما یک شنونده عالی هستید؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">&nbsp;آیا جزئیات را کامل می بینید؟ </span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">آیا شما در مقایسه با بسیاری از کسانی که در اطراف شما هستند بهتر متوجه می شوید و مشاهده می کنید؟</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 18px;">#سارا_دیوبند_روانشناس</span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;">&nbsp;</p>', 'article/images/images_4.jpg', 297, 0, '2020-06-09 14:25:49', '2022-07-13 08:54:46.032305', 2);
INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (83, 'انواع شخصیت', 'personality', 'fa', '<p dir="RTL" style="margin-left: 0in; margin-right: 0in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">گاهی اوقات، برخی از ما به دلیل زندگی با افرادی که از نظر این ویژگی شخصی با ما تفاوت دارند، دچار تعارض هایی می شویم. از آن جایی که همیشه تغییر دادن خودمان، راحت تر از تغییر دادن دیگران است، راهکارهای زیر می توانند به ما کمک کنند تا از میزان برون گرایی یا درون گرایی خود بکاهیم و بدین ترتیب با دوست یا اعضای خانواده مان صمیمت و سازگاری بیشتری داشته باشیم.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: left;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">راهکارهایی برای &laquo;برون&zwnj; گراها&raquo;</span></span></strong></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">1. شمردن اعداد</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اگر برون گرا هستیم با خود تمرین کنیم که قبل از بیان کردن کلامی یا انجام عملی تا 10 بشماریم، در این صورت واکنش های معقول تر و سنجیده تری خواهیم داشت، چه بسا در بعضی موارد تصمیم بگیریم که صحبت نکنیم و به دیگران نیز اجازه دهیم تا نظراتشان را مطرح کنند.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">2. فرصت دادن به دیگران</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">می توانیم از یکی از اعضای خانواده یا دوست صمیمی مان تقاضا کنیم که هر گاه در جمعی زیاد صحبت می کنیم و به دیگران اجازه حرف زدن نمی دهیم، با یک رفتار رمزی ما را متوجه این موضوع کند.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">3- شناختن علاقه فردی</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">یک انسان هرقدر هم برون گرا باشد، باز هم فعالیت های انفرادی هست که از انجام آن لذت ببرد. شناسایی این علاقه و پرداختن به آن کمک می کند تا افراد برون گرا لذت بردن از تنهایی را نیز تجربه کنند و از تنهایی بی تاب نشوند.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><br />&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: center;"><img src="../../../../../upload/images/2020/inrrovert2.jpg" alt="" /></p>
<p dir="rtl" style="text-align: justify;"><strong><span dir="RTL" lang="FA" style="font-size: 14.0pt;">&nbsp;&nbsp;</span></strong></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><strong><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">راهکارهایی برای &laquo;درون&zwnj; گرا&raquo;</span></span></strong></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">1. شرکت در فعالیت های جمعی مورد علاقه</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">ممکن است از این که به مهمانی یا گردش برویم خوشمان نیاید. اما به هر حال هر یک از ما به موضوعاتی مانند یک فعالیت هنری (مانند نقاشی) یا ورزشی (مثل والیبال) و ... علاقه مندیم و از انجام دادن آن ها لذت می بریم. بهتر است که این کارها را به صورت گروهی انجام دهیم. در نظر داشته باشیم که منظور از ارتباط اجتماعی فقط شرکت در مهمانی نیست.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: justify;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">2. بالا بردن اطلاعات</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">گاهی اوقات اتفاق می افتد که ما در جمعی حضور داریم، همه در حال حرف زدن هستند و ما احساس می کنیم که هیچ حرفی برای گفتن نداریم. بهتر است قبل از حضور در جمع موضوعات احتمالی مورد بحث را پیش بینی کرده و در مورد آن ها اطلاعاتی به دست آوریم.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">3. ابراز احساسات در خانواده</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">برای این که بتوانیم احساسات مثبت و منفی خود را به دیگران ابراز کنیم، باید این کار را از صمیمی ترین عضو خانواده یا دوستمان شروع کنیم. اگر ما خود را موظف کنیم که هر روز حداقل یک بار در مورد احساساتمان با شخصی صحبت کنیم، کم کم به این کار عادت خواهیم کرد.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;">&nbsp;</p>
<p dir="rtl" style="text-align: justify;"><strong><span dir="RTL" lang="FA" style="font-size: 14.0pt;">برخی از ویژگی های دو گروه برون گرا و درون گرا در جدول زیر آمده است:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></strong></p>
<p dir="rtl" style="text-align: justify;">&nbsp;</p>
<table class="MsoTableGrid" dir="rtl" style="border-collapse: collapse; border: solid windowtext 1.0pt; margin-left: .5in;" border="1" cellspacing="0" align="right">
<tbody>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">برون گرا</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">درون گرا</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">افرادی اجتماعی، دوستدار دیگران، باجرأت، فعال، سرخوش، با انرژی، خوشبین، دوستدار هیجان و تحرک و امیدوار به موقعیت هایشان و آینده، کنجکاو و در عمل</span></span> <span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">قاطع، فعال و اهل گفتگو هستند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">افرادی محافظه کار، مستقل، تک رو، خوددار، یکنواخت هستند و ترجیح می دهند کارهایشان را به صورت فردی پیش ببرند.</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">از دیگران انرژی می گیرند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">از درون خود انرژی می گیرند.</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">با صدای بلند فکر می کنند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">موضوعات را در ذهن خود ارزیابی می کنند.</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">دوست دارند مرکز توجه باشند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">دوست ندارند مرکز توجه باشند.</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اطلاعات شخصی خود را به راحتی با دیگران در میان می گذارند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اطلاعات شخصی را به ندرت با دیگران در میان می گذارند</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اول عمل و بعد فکر می کنند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">قبل از هر عملی فکر می کنند.</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">بیشتر از گوش دادن، حرف می زنند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">بیشتر گوش می دهند و کمتر حرف می زنند.</span></span></span></span></p>
</td>
</tr>
<tr>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">مشتاقانه با دیگران ارتباط برقرار می کنند.</span></span></span></span></p>
</td>
<td style="vertical-align: top; width: 231.05pt;">
<p dir="RTL" style="text-align: right;"><span style="font-size: 11pt;"><span style="font-family: Calibri,sans-serif;"><span style="font-size: 14.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اشتیاقشان را برای خود نگه می دارند.</span></span></span></span></p>
</td>
</tr>
</tbody>
</table>
<p dir="RTL" style="margin-left: 0in; margin-right: 0.5in; text-align: right;">&nbsp;</p>
