INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (127, 'فیلم آموزش مهارت کنترل هوس مواد مخدر24', 'فیلم-آموزش-مهارت-کنترل-هوس-مواد-مخدر24', 'fa', '<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:right"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:14.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">درمان اعتیاد یا ترک مواد مخدر، بسیار سخت ولی کاملاً ممکن است که &quot;خماری پس دادن&quot; یا &quot;متادون خوردن&quot; جزئی بسیار کوچک از این مسیر پر پیچ و خم است چرا که این بیماری غولی ست با دو سر، که بعد از خفه شدن سر کوچک آن که خماری باشد، تازه سر بزرگ آن ظاهر شده و هر لحظه فردِ غافل و ناآگاه را به زمین می زند؛ یعنی &quot;هوسِ&quot; مصرف مواد و تمایل به تجربه مجدد اثرات قبلی! </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:right"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:14.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">پس برای درمان اصولی و حرفه ای اعتیاد چاره ای جز توانمند شدن در &quot;کنترل هوس&quot; نداریم و این موضوع یکی از اصلی ترین برنامه ها در درمان همیشگیِ اعتیاد است.&nbsp;&nbsp;&nbsp; </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:right"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:14.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">دکتر محمد اسحاقی- &nbsp;مرکز درمان اعتیاد ثامن</span></span></span></span></p>

