INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (153, 'فیلم آموزشی معجزه شکرگزاری', 'secret', 'fa', '<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 16.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">شکرگزاری،همان عاملی که باعث می شود شما به درجه ای از موفقیت دست پیدا کنید که غیر قابل تصور است.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 16.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">امیدوارم که هیچ وقت هیچ وقت یاد خدا را از یادمان نبریم.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: justify;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 16.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">پس خدایا شکرت که به یادت هستیم .</span></span></span></span></p>
