INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (154, 'جلسه مقدماتی1- معجزه شکرگزاری', 'جلسه-مقدماتی1-معجزه-شکرگزاری', 'fa', '<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;">&nbsp;</span></span><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">با سلام خدمت دوستان گرامی .</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">معجزه شکرگزاری از همین الان شروع شد . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">لطفا جملات گفته شده در طول درس را چند بار بخوانید و جملاتی که درون پرانتز 2 تایی </span></span><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">قرار دارد را</span></span><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;"> چندین بار بخوانید و روی آن تمرکز و تفکر کنید و حتی شده آنرا حفظ کنید !!</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">((چنین نیست که خداوند در شکر را برای بنده ای باز کند اما در نعمت را ببندد .)) &nbsp;حضرت علی ( ع ) </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">اینطور بخوانید :</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">((چنین نیست که خداوند در شکر را برای بنده ای باز کند اما در نعمت را ببندد .)) &nbsp;</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">((چنین نیست که خداوند در شکر را برای بنده ای باز کند اما در نعمت را ببندد .)) &nbsp;</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">((چنین نیست که خداوند در شکر را برای بنده ای باز کند اما در نعمت را ببندد .)) &nbsp;</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">لطفا به همین صورت ادامه دهید .</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">((شکرگزاری به درگاه الهی یکی از مهم ترین وظایف انسان است. ))</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span dir="LTR" style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;"><img src="/upload/images/2021/magical-0-1.jpg" style="height:330px; width:512px" /></span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">نعمات و برکات خداوند آن قدر زیاد است که قابل شمارش نیست و فقط با شکرگزاری است که می توان بـه نعمات الهی دست یافت . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">(( قدرت شکرگزاری تمام موارد منفی زندگی را از بین می برد .))</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">&nbsp;زمانیکه حواس خود را متوجه شکرگزاری کنی ، آن را بر زبان بیاوری یا احساسش کنی ، درواقع فرکانس انرژی خود را به یکی از قوی ترین و بالاترین فرکانس های موجود تبدیل می کنی . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">(( همان طور که طبق قانون جذب هر چیزی مشابه خود را جذب می کند ، شکرگزاری در برابر نعمات خدا نیز انرژی مشابه خود را جذب می کند.))</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">&nbsp;شکرگزاری مطلق به مفهوم همیشه به یاد خدا بودن و گام برداشتن در مسیر الهی است . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">(( فرد شاکر درباره ی برکات هستی و سرچشمه ی فیض و نعمات الهی فکر می کند و به چنان باور و ایمانی می رسد که رشته ی وجودش به پروردگار متصل می شود ؛ در نتیجه هرگز لحظه ای از عمر خود را برای موارد منفی هدر نمی دهد و در جهت کسب کمالات و متعالی شدن تلاش می کند .)) </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">(( در قرآن کریم اهمیت شکرگزاری و ذکر پروردگار یکسان است .))</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">فاذكرونی اذكركم واشكر والی ولا تكفرون بقره / </span></span><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">۱۵۲ </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">&laquo; </span></span><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">پس به یاد من باشید ، تا به یاد شما باشم و شکر مرا گویید و کفران نکنید . &raquo; </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">توجه داشته باشید که در این آیه ، (( یاد خدا و شکرگزاری به درگاه او صرفاً نمی تواند کلامی باشد ، بلکه هدف از یاد خدا آن است که از طریق پاکسازی روح و روان ، مثبت اندیشی و از بین بردن خصلتهای منفی ، خودت را برای پذیرش رحمت و برکات الهی آماده کنی . ))</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">شکرگزاری ، نوعی توفیق و نعمت الهی است که لازم است به پاس این شکرگزاری ، باز هم شکری به جا آورد . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">امام سجاد ( ع ) می فرمایند : </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">چگونه می توانم حق شکر تو را به جای آورم درحالی که همین شکر مـن نـیازمند شکری دیگر است و هر زمان که می گویم الحمدالله ، بر من فرض است که بابت همین توفیق شکرگزاری بگویم : الحمدالله .</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">از آنجا که شکرگزاری کلامی و زبانی ، دشوار نیست و هر کسی می تواند آن را انجام دهد ، پس منظور شکر عملی است که جایگاهی ویژه دارد و به معنی استفاده ی بجا و صحیح از نعمات الهی در مسیر همان اهدافی است که نعمتها برای آنها آفریده و اعطا شده اند و بدیهی است تعداد کسانی که مواهب الهی را معمولاً در جای خود به کار می برند و به درستی از آن استفاده کنند ، اندک است . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">(( در واقع شکر و سپاس سه مرحله دارد : </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">اول ، شکرگزاری قلبی ؛ یعنی تفکر درباره ی نعمت و خشنودی از آن . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">دوم ، شکرگزاری زبانی که ثناگویی از نعمت دهنده ی اصـلی و تشکـر از واسـطه ى فیض است . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">و سوم ، شکرگزاری اعضا و جوارح ، یعنی قدردانی در برابر نعمت ، که در اصـل همان شکرگزاری عملی است و جایگاهی خاص دارد .))</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">در کتابی که هم اکنون در دست دارید ، راندا برن ، نویسنده ی کتاب های راز و راز شکرگزاری ، باز هم به موضوع شکرگزاری پرداخته و تمرینهایی در این زمینه ارائه کرده است که شما کمک خـواهـد کـرد ایـن عـمل ضروری و معجزه آسا ملکه ی ذهنتان شود . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">&laquo; نفیسه معتکف &raquo;</span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><strong><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;"><span style="color:red">رازی بزرگ آشکار می شود</span></span></span></strong></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">عبارت زیر برگرفته از کتاب مقدس است ، که عده ی زیادی از مردم در طی قرون متمادی برداشتی غلط از آن داشتند : </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">&laquo; هرکس مال و ثروتی دارد ، به او ثروت بیشتری داده می شود و او از وفور نعمت بهره مند خواهد شد . هرکس که مال و منالی نداشته باشد ، حتی آنچه هم دارد از او پس گرفته خواهد شد . &raquo; </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">اعتراف کن که وقتی این عبارت را می خوانی ، ناعادلانه به نظر می رسد . گویی مفهوم عبارت این است که ثروتمندان ، ثروتمندتر و فـقـرا ، فـقیرتر می شوند . اما در این عبارت معمایی هست که باید حل شود ، رازی که باید بر ملا شود و وقتی متوجه مفهوم آن شوی ، درهای دنیایی تازه به روی تو باز خواهد شد . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">جواب این معمای رمزگونه که قرن هاست به ذهن کسی نیامده ، در یک کلمه پنهان است : شکرگزاری . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">پس عبارت این چنین می شود : </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">(( هرکس که شکر نعمت کند ، مـواهب بیشتری به او داده می شود و وفور نعمت بیشتری خـواهـد داشت و کسی که ناشکر باشد ، هر چند نعمت اندکی هم داشته باشد ، آن نعمت از او گرفته خواهد شد . )) </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">با آشکارسازی کلمه ی پنهان ، متن مبهم واضح می شود . دو هزار سال از زمانی که این عبارت ثبت شده است ، می گذرد امـا امـروز هـم بـه هـمان اندازه ی سابق حقیقت دارد . </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">اگر برای شکرگزاری وقت صرف نکنی ، هرگز نعمت بیشتری نخواهی داشت و نعماتی را هم که داری از دست خواهی داد ؛ و وعده ی معجزه که با شکرگزاری رخ می دهد ، در این عبارت نهفته است : </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">اگر شاکر باشی ، نعمت بیشتری به تو داده می شود و تو به وفور نعمت می رسی ؟ </span></span></span></span></p>

<p dir="RTL" style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:11pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;"><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">در قرآن کریم ، در سوره ی ابراهیم ، آیه ی </span></span><span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">۷</span></span> <span style="font-size:16.0pt"><span style="font-family:&quot;Vazir&quot;,&quot;sans-serif&quot;">هم وعـده ی شکرگزاری تایید شده است :</span></span></span></span></p>

