INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (164, 'تقویت تعهد', 'obligation', 'fa', '<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">(( این کار زمانی بهترین تأثیر را دارد که فرد در اواخر مرحله تأمل یا اوایل مرحله اقدام برای تغییر است. ))</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">خوب میخواهم بدانم حالا می خواهید چه کار کنید؟ </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">درباره مصرف شیشه چه تصمیمی دارید؟ </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">برای تغییر این وضعیت چه برنامه ای در نظر دارید؟ </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">ممکن است چه مشکلی در اجرای برنامه های شما به وجود بیاید؟ </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><strong><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">انتخاب آزاد موضوع </span></span></strong></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">(( گرچه پرهیز یکی از اهداف احتمالی مداخله است برخی افراد ممکن است برای قطع مصرف مواد کاملاً آماده نباشد و به کاهش یا کنترل مصرف تمایل نشان دهند. در مدل مصاحبه انگیزشی مسئولیت نهایی تغییر بر عهده خود فرد است و او برای انتخاب هدف مداخله آزادی کامل دارد. نقش درمانگر کمک به مراجع برای تعیین هدف مداخله اولیه است .بخش تعیین هدف را در ادامه ببینید. توجه داشته باشید که چنین اهدافی احتمالاً در طول مسیر مداخله تغییر می کند و هدف اولیه کاهش ممکن است با افزایش اطمینان فرد به امکان پذیر بودن تغییر به طرف قطع مصرف و پرهیز تغییر نماید.))</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">به ترسهای فرد توجه کنید</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">با توجه به جدولی که پر کردید ، به من گفتید موقعیت شما ، جنبه نه چندان خوب کاهش مصرف شیشه است. بزرگترین نگرانی شما از کاهش یا قطع مصرف چیست؟</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: red;">((پاسخ بیمار ))</span></span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">(( برای غلبه بر ترسها از روش &nbsp;حل مسئله کمک بگیرید تا باعث تغییر رفتار شوید هر چند در موارد شدید میتوان با نظر روانپزشک برای یک دوره کوتاه دارو تجویز نمود.)) </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><strong><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">آموزش و توصیه </span></span></strong></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اگر نظر مرا بخواهید قطعاً به شما خواهم گفت، اما در نهایت شما هستید که باید در این باره تصمیم بگیرید.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">آیا این مطالب برای شما روشن بود؟ </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: red;">((پاسخ بیمار ))</span></span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><strong><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">تعیین اهداف</span></span></strong></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">بیایید یک قانون شخصی یا محکم برای مصرف تفننی داشته باشید .</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">برای مثال هفته ای فقط یک بار مقدار مشخص (حداکثر چقدر؟) مصرف می کنم یا هرگز شیشه نمی خرم.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">توجه داشته باشید که اهداف کمک کننده هستند صرف نظر از آن که به آنها دست پیدا کنید یا خیر. </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اهدافی که شما به آن دست پیدا می کند را می توانید خود را تشویق نمایید اما در صورتی که شما نتوانید به هدفی دست پیدا کند باید به آن به چشم یک تجربه آموزنده برای تعیین اهداف آتی نگاه کنید.</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span dir="LTR" style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"></span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"> کاهش مصرف شیشه ، اهداف درمانی کوتاه مدت است .</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">قطع کامل مصرف یا پرهیز از شیشه ، یک هدف منطقی است. </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اگر مصرف نکنید می توانید اطمینان داشته باشید که هیچ یک از مشکلات مرتبط با مصرف را تجربه نخواهید کرد. دلایل خوبی وجود دارد که لااقل یک دوره قطع مصرف و پاکی را امتحان کنید .</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">برای مثال ببینید زندگی بدون مصرف شیشه چگونه است و چه احساسی دارید، یاد بگیرید که چگونه</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">به مصرف شیشه وابسته شده اید، عادات قدیمی خود را بشکنید، تغییر را تجربه کنید و اعتماد به نفس خود را بدست آورید ، رضایت همسر و یا خانواده خود را جلب کنید </span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">(( اگر اطلاعات ارزیابی بیمار نشان دهد که قطع کامل مصرف یا پرهیز از شیشه را در نظر نگرفته است ))</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">البته این تصمیم به خود شما بستگی دارد. اما می خواهم بگویم، تصمیمی که گرفته اید باعث نگرانی من است و اگر&nbsp; تمایل دارید برایتان توضیح می دهم چرا !!</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">الان می خواهم نظر شما را بشنوم :</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: red;">((پاسخ بیمار ))</span></span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>', 'article/images/handshake-close-up-executives.jpg', 9, 0, '2022-01-01 21:20:17', '2022-07-13 13:09:11.406683', 2);
INSERT INTO medicalplus.article_article (id, title, slug, lang, body, image, viewCount, commentCount, created_at, updated_at, user_id) VALUES (165, 'ناآگاهی شخصیت در یک نگاه', 'Character-ignorance', 'fa', '<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: red;">ناآگاهی شخصیت در یک نگاه :</span></span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;">&nbsp;</p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';"><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: red;">ناآگاهی</span></span></span> <span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';"><span style="color: red;">شخصیت پارانوئید</span></span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';">&nbsp;<span dir="LTR" style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">A</span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">. بی اعتمادی و بد گمانی فراگیر نسبت به دیگران که از اوایل بزرگسالی آغاز شده و در زمینه های مختلف به چشم می خورد و با حداقل </span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">۴</span></span> <span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">مورد زیر مشخص می گردد :</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';">&nbsp;<span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">1- </span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">بد گمانی بی پایه و اساس درباره ی اینکه دیگران استثمارگر ، آسیب زننده و یا فریبکارند .</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';">&nbsp;<span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">2- </span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">اشتغال ذهنی با تردیدهای ناموجه درباره ی وفاداری یا قابل اعتماد بودن دوستان و</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';">&nbsp;<span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">3- </span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">عدم تمایل برای اعتماد به سایرین به دلیل ترس بی دلیل از اینکه اطلاعات مغرضانه بر ضد وی به کارخواهند رفت . آشنایان</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';">&nbsp;<span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">4- </span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">در پس اظهارات بی غرضانه یا رویدادهای خوش خیم ، معانی تحقیر آمیز یا تهدید کننده پنهانی بیابد .</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';">&nbsp;<span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">5- </span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">کینه جویی پایدار</span></span></span></span></p>
<p dir="RTL" style="margin-left: 0cm; margin-right: 0cm; text-align: right;"><span style="font-size: 11pt;"><span style="font-family: ''Calibri'',''sans-serif'';">&nbsp;<span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">6- </span></span><span style="font-size: 12.0pt;"><span style="font-family: ''Vazir'',''sans-serif'';">تلقی حمله به منش یا شهرت خود که برای دیگران واضح نبوده و به سرعت نسبت به آنها واکنش خشمگینانه نشان دهد یا به حمله متقابل بپردازد .</span></span></span></span></p>
