INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (12, 'شما در برقراری روابط اجتماعی و بین فردی بسیار توانمند هستید. مهارت های کلامی خوبی دارید و از قرار گرفتن در شرایط مختلف ارتباطی هیچ هراسی ندارید. داوطلب همیشگی مشارکت های گروهی و اجتماعی هستید و از این کار لذت می برید.', 44, null, null, 21);
INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (13, 'شما در برقراری ارتباط اجتماعی خوب و میانه رو هستید. در گروه ها و گفتگو ها شرکت می کنید. شاید در موارد بسیار جزیی احتیاج  باشد تا مهارت های بین فردی خود را افزایش دهید. اگر شرایطی مد نظرتان است که کم رو بوده اید سعی کنید علل آن را شناسایی کنید و نسبت به رفع ان اقدام کنید.', 88, null, null, 21);
INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (14, 'شما تا اندازه ی زیادی کم رو هستید. نمی توانید به راحتی خواسته هایتان را مطرح کنید و در گروه ها ی اجتماعی مشارکت داشته باشید. ادامه ی این روند باعث آسیب های جدی در آینده خواهد شد و شما از رسیدن به بسیاری از خواسته ها و اهدافتان باز می مانید. اگر برای اصلاح این وضعیت احساس ناتوانی می کنیدپیشنهاد می شود  از مشاور متخصص کمک بگیرید.', 132, null, null, 21);
INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (15, 'شما شدیدا کم رو هستید و احتمالا مشکلات زیادی در زمینه ی روابط بین فردی و انجام کارهای گروهی دارید برای اصلاح افکار منفی در روابط، کم کردن هراس از مشارکت و افزایش مهارت های مکالمه لازم است در اسرع وقت از یک مشاور متخصص کمک بگیرید تا ضمن ریشه یابی مشکل در درمان کم رویی همراه و راهنمای شما باشد.', 176, null, null, 21);
INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (16, 'کمترین میزان پرخاشگری در رفتار شما وجود دارد که می توان گفت واکنش طبیعی افراد سالم است. رفتار های شما در مقابل موقعیت های خشم بر انگیز اغلب کنترل شده و از روی تفکر است. اتفاقات محیط پیرامون شما و رفتارهای دیگران به راحتی شما را تحریک نمی کند، این یکی از ویژگی های خوب شماست. شما به راحتی می توانید عوامل هیجانی تاثیر گذار بر رفتارتان را کنترل کنید.', 30, null, null, 22);
INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (17, 'پرخاشگری شما در حد متوسط است. همه ما ممکن است میزانی از پرخاشگری را در رفتارمان داشته باشیم و گهگاهی بروز پیدا کند اما این رفتار زمانی مشکل ساز می شود که بسیار شدید و مکرر باشد و دفعات آن افزایش یابد و یا به طور نامناسبی ابراز شود. باید کمی در رفتارتان دقیق تر شوید. چرا که کنترل نکردن این روند موجب آسیب های جدی در آینده ی نزدیک خواهد شد. لازم است مدیریت موثر خشم را از طریق یادگیری مهارت جراتمندی تغییر خود گویه های منفی و خصومت آمیز تغییر باورهای غیر منطقی و به کارگیری راهبردهای رفتاری متنوع بیاموزید و در صورت لزوم از مشاور متخصص کمک بگیرید.', 60, null, null, 22);
INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (18, 'آزمون حاضر بالاترین سطح پرخاشگری را در شما گزارش می دهد. در موقعیت های خشم برانگیز به راحتی کنترل خود را از دست می دهید و رفتارهای پرخاشگرانه بروز می دهید. باورهای مخرب و آزار دهنده ای در ذهن شماست. ادامه ی این روند علاوه بر آسیب جدی به دیگران موجب بروز مشکلات جسمی و روانی در خودتان خواهد شد. در اسرع وقت برا ی کنترل وضعیت موجود و پیشگیری از آسیب های جدی تر به مشاور متخصص مراجعه کنید.', 90, null, null, 22);
INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (19, 'رفتارهای وسواس گونه در شما کم است یا اصلا وجود ندارد، زیاد نگران نباشید، انجام بعضی کارهای تکراری و رفتارهای خاصی که به صورت عادت در ﺁمده، اگر در زندگی تان اختلال ایجاد نکند طبیعی است، با انجام رفتارهای جایگزین و کم کردن حساسیت ها می توان بعضی رفتارها را اصلاح کرد.', 10, null, null, 23);
