INSERT INTO psychology_testresult (id, result, grade, created_at, updated_at, test_id) VALUES (44, 'شما به صورت نسبی به بلوغ عاطفی رسیده اید یعنی از نظر احساسی به طور کامل پخته نشده اید وگاهی در برابر مشکلات عاطفی خود نمی توانید منطقی تصمیم گیری کنید. رسیدن به بلوغ عاطفی فرایندی پویا است و شما دائما می توانید در طول زندگی روی آن کار کنید و آن را ارتقا ببخشید. برای دستیابی به این مهم حتما برنامه ریزی و تمرین کنید و در صورت لزوم از یک مشاور کمک بگیرید.', 116, null, null, 31);
